<?phpuse yii\helpers\Html;use yii\widgets\ActiveForm;/* @var $this yii\web\View *//* @var $model common\models\book *//* @var $form yii\widgets\ActiveForm */?><div class="book-form">    <?php $form = ActiveForm::begin([        'options' => ['enctype' => 'multipart/form-data']    ]); ?>    <?if($model->hasErrors()):?>        <div class="error-summary"><?=$model->errors[0];?></div>    <?endif;?>    <?    $authors = common\models\Author::find()->all();    $items = \yii\helpers\ArrayHelper::map($authors,'id','name');    echo $form->field($model, 'author_id')->dropDownList($items) ?>    <?= $form->field($model, 'description')->textarea(['rows' => 6]) ?>    <?= $form->field($model, 'image')->fileInput(['accept' => 'image/*'])->label('Фото') ?>    <div class="form-group">        <?= Html::submitButton($model->isNewRecord ? 'Create' : 'Update', ['class' => $model->isNewRecord ? 'btn btn-success' : 'btn btn-primary']) ?>    </div>    <?php ActiveForm::end(); ?></div>