<?phpuse yii\helpers\Html;use yii\grid\GridView;/* @var $this yii\web\View *//* @var $searchModel frontend\models\bookSearch *//* @var $dataProvider yii\data\ActiveDataProvider */$this->title = 'Books';$this->params['breadcrumbs'][] = $this->title;?><div class="book-index">    <h1><?= Html::encode($this->title) ?></h1>    <?php // echo $this->render('_search', ['model' => $searchModel]); ?>    <p>        <?= Html::a('Create Book', ['create'], ['class' => 'btn btn-success']) ?>    </p>    <?= GridView::widget([        'dataProvider' => $dataProvider,        'filterModel' => $searchModel,        'columns' => [            ['class' => 'yii\grid\SerialColumn'],            'id',            [                'attribute' => 'author_id',                'value' => function($model){                    return $model->AuthorName;                },                'label' => 'Автор'            ],            'description:ntext',            array(                'format' => 'raw',                'label'=>'Фото',                'value' => function($model){                    return '<img src="data:image/jpeg;charset=utf-8;base64,' . base64_encode($model->image).'" style="max-width:150px">';                }            ),            ['class' => 'yii\grid\ActionColumn'],        ],    ]); ?></div>