<?php    namespace frontend\controllers;    use common\models\Author;    use common\models\Book;    use yii\web\Controller;    use \yii\helpers\Json;    use yii\web\NotFoundHttpException;    class ApiController extends Controller    {        public function actionList(){           \Yii::$app->response->statusCode = 200;            return Json::encode(Book::find()->all());        }        public function actionGet($id){            $book = Book::findOne(['id'=>$id]);            if($book){                \Yii::$app->response->statusCode = 200;                return Json::encode($book);            }            throw new NotFoundHttpException();        }        /**         * Id книги в задании отсутствует в строке запроса, я предполагаю что он находится в POST         */        public function actionUpdate(){            $id = (int) \Yii::$app->request->post('id');            $model = Book::findOne(['id'=>$id]);            if ($model->load(\Yii::$app->request->post()) && $model->save()) {                \Yii::$app->response->statusCode = 200;            }            else                throw new NotFoundHttpException;        }        public function actionDelete($id){            $model = Book::findOne(['id'=>$id]);            if(!$model || !$model->delete())                throw new NotFoundHttpException;            else                \Yii::$app->response->statusCode = 200;        }    }